    public boolean arraysAreEquivalent(byte[] a1, byte[] a2) {
        if (a1 == a2)
            return true;

        if (null == a1)
            return false;
        else if (null == a2)
            return false;
        else if (a1.length != a2.length)
            return false;

        for (int i = 0; i < a1.length; i++) {
            if (a1[i] != a2[i])
                return false;
        }

        return true;
    }

